import React, { useState } from 'react';
import './App.css';

function App() {

    const [city, setCity] = useState<string>('')
    // console.log(city)
    const [error, setError] = useState<null | string>(null)
    const [weather, setWeather] = useState<{ temp: number, description: string } | null>(null);

    const fetchWeather = () => {
        // const city='Minsk'
        const apiKey = '1ce2c8f1225f14bf33b893989c1548cf';
        fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}`)
            .then(response => response.json())
            .then(json => {
                if (json.cod === "404") {
                    setError('City not found'); // Устанавливаем ошибку, если город не найден                
                } else {
                    setWeather(json);
                     setError(null);
                    // Сбрасываем ошибку, если запрос успешен               
                }
            }).catch(error => {
                console.error('Ошибка:', error); setError('An error occurred'); // Общая ошибка на случай других проблем    
            });
    }



    return (
        <div className="App">
            <h1>Weather App</h1>
            <input type="text" onChange={(e) => setCity(e.currentTarget.value)} />
            <button onClick={fetchWeather}>Get weather</button>

            {error && <p style={{ color: 'red' }}>{error}</p>} {/* Отображаем ошибку */}

        </div>
    );
}

export default App
